using UnityEngine;using System.Collections;public class Lebensanzeige : MonoBehaviour {    private GameObject lebensanzeige;    private RectTransform transform;    public int leben = 0;	// Use this for initialization	void Start () {        lebensanzeige = GameObject.FindWithTag("Lebensanzeige");        transform = lebensanzeige.GetComponent<RectTransform>();	}		// Update is called once per frame	void OnTriggerEnter (Collider other) {        if(other.gameObject.tag == "Player")        {            var width = transform.sizeDelta.x;            leben++;            transform.sizeDelta = new Vector2(width + 500, 400);           // transform.anchoredPosition = new Vector2(120, -50);            Destroy(gameObject);        }       	}}